import './BtnRegister.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { IoIosPeople } from 'react-icons/io';

// Button Component
export default function ReturnButton() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-hmBtn-dash'>
  <ViewButton />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-hmBtn-dash-sm'>
  <ViewButton />
 </div>
)
export const ViewButton = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/dash/register');
 };
 return (
  <button className='Btn' onClick={handlepath}>
   <span>Ajouter agent</span>
   <span><IoIosPeople size={'1.7em'} color='#0276aa' /></span>
  </button>
 );
};
