import React from 'react';
import './Avatar.css';
// import { IoIosPerson } from 'react-icons/io';
import Media from 'react-media';
import ReturnName from './Name';

var firstLetter = '';

// Avatar IMAGE VIew
export default function ReturnAvataR() {

 const first = 'Mabiala';
 const last = 'Henry';
 const letter = first[0];

 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall firstName={first} lastName={last} />}
     {matches.medium && <ScreenLarge firstName={first} lastName={last} />}
     {matches.large && <ScreenLarge firstName={first} lastName={last} />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = (props) => {
 return (
  <div className='wrp-avatar-profil-dash'>
   <div className='avatar-circle-profil-dash'>
    <h1>{props.letter}</h1>
   </div>
   <ReturnName firstName={props.firstName} lastName={props.lastName} />
  </div>
 );
}
export const ScreenSmall = (props) => (
 <div className='wrp-avatar-profil-dash-sm'>
  <div className='avatar-circle-profil-dash-sm'>
   <h1>{props.letter}</h1>
  </div>

  <ReturnName firstName={props.firstName} lastName={props.lastName} />
 </div>
);
