import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, getDoc, updateDoc, serverTimestamp } from "firebase/firestore";

import { db } from '../../firebase';

export let prixSuccess = 0;

// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();
 const [getPin, setGetPin] = React.useState('');
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 React.useEffect(async () => {

  const docRefPin = doc(db, "ungama", "money");
  const docSnap = await getDoc(docRefPin);

  if (docSnap.exists()) {
   setGetPin(docSnap.data().pin);
  };

  const docagentRef = doc(db, "agent", phoneX);
  const docagentSnap = await getDoc(docagentRef);

  if (docagentSnap.exists()) {
   setFirst(docagentSnap.data().firstname);
   setLast(docagentSnap.data().lastname);
  };

 }, []);

 const onSubmit = async (data) => {

  if (getPin == data.code && data.code.length == 6) {

   if (data.count > 200000) {
    window.alert(`La valeur est superieur à 200000`);
    reset();

   } else {

    sendAgentMoney(Math.floor(Number(data.count)), Math.floor(Number(data.count)), phoneX)
    updateAdmin(Math.floor(Number(data.count)), Math.floor(Number(data.count)), first, last)

    prixSuccess = data.count;
    navigation('/send/success');
   }

  } else {
   prixSuccess = data.count;
   window.alert(`Erreur de code d'accès invalide`)
   reset();
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'USD'}
   />
   <input type={'text'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};

async function sendAgentMoney(solde, money, numPhone) {

 const washingtonRef = doc(db, "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(solde),
  money: money,
  unite: 'usd',
  delay: serverTimestamp()
 });

};
async function updateAdmin(solde, money, FirstName, LastName) {

 const washingtonRef = doc(db, "muunganomoney", "money");
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(-(solde)),
  money: money,
  buyusd: increment(solde),
  unite: 'usd',
  transfertime: serverTimestamp(),
  user: FirstName + ' ' + LastName,
 });

};
