import React from 'react';
import moment from 'moment';
import { doc, onSnapshot } from "firebase/firestore";
import { db } from '../../firebase';
import './Commission.css';

let value = 0;

export const CommissionCDFprix = () => {

 const [prix, setPrix] = React.useState([]);
 React.useEffect(async () => {
  try {
   onSnapshot(doc(db, "muungano", "commission"), (doc) => {
    setPrix(doc.data().cdf);
   });
  } catch {
   window.console.log('erreur disponible')
  }
 }, []);

 return (
  <div className='wrp-time-sub'>
   {[...Array(moment().date()).keys()].map(index => {

    value = index;
    value += 1;

    return (
     <h1>{prix[value]}</h1>
    )
   })
   }
  </div>
 );
};
export const CommissionUSDprix = () => {

 const [prix, setPrix] = React.useState([]);
 React.useEffect(async () => {

  try {
   onSnapshot(doc(db, "muungano", "commission"), (doc) => {
    setPrix(doc.data().usd);
   });
  } catch {
   window.console.log('Erreur disponible');
  }

 }, []);

 return (
  <div className='wrp-time-sub' >
   {[...Array(moment().date()).keys()].map(index => {

    value = index;
    value += 1;
    let toPrix = Math.round(prix[value] * 100) / 100;

    return (
     <h1>{toPrix.toFixed(2)}</h1>
    )
   })
   }
  </div>
 );
};
