import React from 'react';
import MaterialUIPickers from './Date';
import { Button } from 'react-bootstrap';
import './Form.css';
import { useForm } from 'react-hook-form';

import { db } from '../firebase';
import { collection, addDoc } from 'firebase/firestore';
import { BsCamera2 } from 'react-icons/bs';

import ReturnPrint from '../Print/Print';
import { BsFillPrinterFill } from 'react-icons/bs';

// Form input field compont 
export default function RetuRnForm(props) {

 const { register, handleSubmit, formState: { errors }, formState, watch } = useForm({ mode: 'onTouched' });
 const { isSubmitting, isValid } = formState;
 const [name, postname, firstname, sexe, cell, federation, section] = watch(['name', 'postname', 'firstname', 'sex', 'cell', 'section', 'federation']);

 const onSubmit = async data => {
  await addDoc(collection(db, 'members'), { data });
 };


 let videoRef = React.useRef(null);
 let photoRef = React.useRef(null);

 const getUserCamera = () => {
  navigator.mediaDevices.getUserMedia({

   video: true

  }).then((stream) => {
   let video = videoRef.current;
   video.srcObject = stream;
   video.play();
  }).catch((error) => { console.error(error) })
 }

 React.useEffect(() => {
  getUserCamera();
 }, [videoRef]);

 const takePicture = () => {
  let width = 130;
  // let height = 200;
  let height = width / (11 / 10);
  let photo = photoRef.current;
  let video = videoRef.current;

  photo.width = width;
  photo.height = height;

  let ctx = photo.getContext('2d');
  ctx.drawImage(video, 0, 0, photo.width, photo.height);
 }


 const handleprint = (event) => {
  event.preventDefault();
  window.print();
 }

 return (
  <div className='wrp-form-field-add'>

   <div classNam='view-form-right'>
    <form onSubmit={handleSubmit(onSubmit)}>
     <div className='field-box-input'>

      <div className='form-field-add'>
       <label htmlFor='name'>Nom</label>
       <input type='text' name='name' id='name' {...register("name", { required: true, maxLength: 255 })} />

      </div>

      <div className='form-field-add'>
       <label>Post-nom</label>
       <input type='text' name='postname' id='postname' {...register("postname", { required: true, maxLength: 255 })} />
      </div>

     </div>

     <div className='field-box-input'>

