import React from 'react';
import './Avatar.css';
// import { IoIosPerson } from 'react-icons/io';
import Media from 'react-media';
import ReturnName from './Name';

var firstLetter = '';

// Avatar IMAGE VIew
export default function ReturnAvataR() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall firstName={'Tshilela'} lastName={'Hendy'} />}
     {matches.medium && <ScreenLarge firstName={'Tshilela'} lastName={'Hendy'} />}
     {matches.large && <ScreenLarge firstName={'Tshilela'} lastName={'Hendy'} />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = (props) => {
 return (
  <div className='wrp-avatar-profil-dash'>
   <div className='avatar-circle-profil-dash'>
    <h1>{props.letter}</h1>
   </div>
   <ReturnName firstName={props.firstName} lastName={props.lastName} />
  </div>
 );
}
export const ScreenSmall = (props) => (
 <div className='wrp-avatar-profil-dash-sm'>
  <div className='avatar-circle-profil-dash-sm'>
   <h1>{props.letter}</h1>
  </div>

  <ReturnName firstName={props.firstName} lastName={props.lastName} />
 </div>
);
