import React from 'react';
import './BtnAdd.css';
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { db } from '../firebase';
import Media from 'react-media';

// Return Add Button
export default function ReturnAddBtnMoney() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btnadd-dash'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btnadd-dash-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 const prixCdf = 30000;
 const prixUsd = 200;

 return (
  <button className='Btn'
   onClick={addMoneyInWithUngamaMoney(prixCdf, prixUsd)}>
   Ajouter Money
  </button>
 )
};


// Add money Ungama
async function addMoneyInWithUngamaMoney(cdf, usd) {

 const docRef = doc(db, "ungama", "money");
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  // Set the "capital" field of the city 'DC'
  await updateDoc(docRef, {
   fc: Number(docSnap.data().fc) + Number(cdf),
   usd: Number(docSnap.data().usd) + Number(usd),
  });

 }

}
