import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';

import { doc, getDoc } from "firebase/firestore";
import { db } from '../../firebase';


// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);

export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const onSubmit = async (data) => {

  if (verifierCode() == data.code) {
   return;
  } else {
   window.alert(`Erreur Code Invalid.`);
  };

  navigation('/send/success');
  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type='text' name='count' placeholder='$' {...register('count', { require: true })} />
   <input type='text'
    {...register('code', { require: true })}
    placeholder='••••••' name='code' />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};

// verifier code access input
export const verifierCode = async () => {

 const docRef = doc(db, "ungama", "money");
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  return docSnap.data().code;
 } else {
  // doc.data() will be undefined in this case
  return "not found";
 }

};
export const sendMoneyAgent = async (count, phoneAgent) => {

 const docRef = doc(db, "agent", phoneAgent);
 const docSnap = await getDoc(docRef);


 const washingtonRef = doc(db, "agent", phoneAgent);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  fc: true
 });


};
