 const [withdraw, setWithdraw] = React.useState(exchAnge);
 const obj = { value: 0 };
 React.useEffect(() => {

  let target = document.querySelector('.valueTarget');
  TweenLite.to(obj, 1, {
   value: withdraw,
   roundProps: { value: 100 },
   onUpdate: () => target.innerHTML = obj.value
  })

 }, []);

 return (
  <div className='wrp-prix-exchange'>
   <p className='valueTarget'></p>
  </div>
 );
};
