import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, deleteDoc, getDocs, collection } from "firebase/firestore";
import { db } from '../../firebase';


let arrayPush = new Array();


// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
}


export const ScreenLarge = () => (
 <div className='wrp-btn-delete'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-delete-sm'>
  <CancelBtn />
  <DeleteBtn />
 </div>
);

export const CancelBtn = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('PHONE_USER_REMOVE_NEXT', JSON.stringify(null));
  navigation('/dash');
 }

 return (
  <button onClick={handlepath} className='Btn'>Anuller</button>
 );
};
export const DeleteBtn = () => {


 React.useEffect(async () => {
  const clientSnapshot = await getDocs(collection(db, "client"));
  clientSnapshot.forEach((doc) => { arrayPush.push(doc.id); });

 }, []);

 const isphoneInDocs = arrayPush.some(value => value == JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE_NEXT')));
 const handleDelete = async (event) => {
  event.preventDefault();

  window.localStorage.setItem('PHONE_USER_REMOVE', JSON.stringify(null));
  await deleteDoc(doc(db, isphoneInDocs ? "client" : "agent", JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE_NEXT'))));
 }

 return (
  <button onClick={handleDelete} className='Btn-Red'>Supprimer</button>
 );
};
