 const getUserCamera = () => {
  navigator.mediaDevices.getUserMedia({

   video: true

  }).then((stream) => {
   let video = videoRef.current;
   video.srcObject = stream;
   video.play();
  }).catch((error) => { console.log })
 }


 React.useEffect(() => {
  getUserCamera();
 }, [videoRef]);


