import React from 'react';
import REturnSendMoney from './Send';
import { auth } from '../firebase';
import SignUp from '../Sign/Main';
import GridLoader from 'react-spinners/GridLoader';

// Export Send Money
export default function SendUsd() {

 const user = auth.currentUser;
 const [loading, setLoading] = React.useState(true);
 const [people, setPeople] = React.useState(user);

 React.useEffect(() => {

  // setLoading(true);
  setTimeout(() => {
   setLoading(false);
  }, 3600);

  if (user) {
   setTimeout(() => {
    setPeople(true);
   }, 600);

  }

 }, []);

 return (
  <div>
   {
    loading &&
    <div className='App-loading'>
     <GridLoader
      size={15}
      color={'#00b8d4'}
      loading={loading}
     />
    </div>
   }

   <>
    {people ? <REturnSendMoney /> : <SignUp />}
   </>
  </div>
 );
}
