import './LinkRemove.css';
import { Link } from 'react-router-dom';
import Media from 'react-media';

// Return Link Component 
export default function ReturnLinKRemove() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-link-remove'>
  <VieWRemove />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-link-remove-sm'>
  <VieWRemove />
 </div>
);
export const VieWRemove = () => {

 const handleNull = (event) => {
  event.preventDefault();
  window.localStorage.setItem('?bu%^%ild--#', JSON.stringify(false));
 };
 return (
  <div onClick={handleNull}>
   <Link style={{ textDecoration: 'none' }} to='/remove'>
    <p>
     Supprimer client
   </p>
   </Link>
  </div>
 );
};
