  // Add a new document in collection "cities"
  await setDoc(doc(db, `${JSON.parse(window.localStorage.getItem('USER'))}`, userDocsFiat), {
    firstname: name,
    lastname: last,
    staf: staf,
    residence: residence,
    dwelling: dwelling,
