import React from 'react';
import './Input.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';

import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';

// Firebase Auth for phone
import { collection, getDocs } from "firebase/firestore";
import FadeLoader from 'react-spinners/FadeLoader';
import { db } from '../../firebase';
import TextField from '@mui/material/TextField';

export let sendPhone = '';

// Input Field form
export default function REturnInPutCode() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='input-phone-register'>
  <FormDataInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='input-phone-register'>
  <FormDataInput />
 </div>
);

export const FormDataInput = () => {

 let pushClient = new Array();
 let pushAgent = new Array();
 const [load, setLoad] = React.useState(false);

 const regular = /[a-z]+/;
 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm({
  defaultValues: { phone: '+243' }
 });

 const [loading, setLoading] = React.useState(false);
 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClose = () => {
  setOpen(false);
 };

 const onSubmit = async (data) => {

  setLoad(true);

  if (data.phone.length != 13 || regular.test(data.phone)) {
   setOpen(true);
   setLoad(false);
   reset();
  } else {

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    pushClient.push(doc.id);
   });
   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    pushAgent.push(doc.id);
   });

   const verifierClient = pushClient.some(value => value == data.phone);
   const verifierAgent = pushAgent.some(value => value == data.phone);

   if (verifierClient || verifierAgent) {

    sendPhone = data.phone;
    window.localStorage.setItem('**adm@@ph$$&-@#', JSON.stringify(data.phone));

    window.setTimeout(() => {
     navigation('/exchange');
     setLoading(false);
    }, 2100);

    reset();

   } else {

    setLoading(false);
    setLoad(false);
    setOpen(true);
    reset();

   };

  };
 };

 return (
  <>
   <div className='zindex-theme'>
    <Backdrop
     sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
     open={load}>

     <CircularProgress color="inherit" />
    </Backdrop>
   </div>

   <form onSubmit={handleSubmit(onSubmit)}>
    <FormControl
     sx={{ width: '100%' }}

     variant="standard">
     <InputLabel htmlFor="standard-adornment-password">Téléphone</InputLabel>

     <Controller
      name="phone"
      defaultValue='+243'
      control={control}
      render={({ field }) =>

       // <Input
       //  id="standard-adornment-password"
       //  {...field}

       //  className='text-input-size'
       //  type='phone'
       // />

       <TextField
        label="Standard warning"
        variant="standard"
        color="primary"
        {...field}
        focused
       />

      }
     />

    </FormControl>

    <Dialog
     fullWidth={fullWidth}
     maxWidth={maxWidth}
     open={open}
     onClose={handleClose}
    >
     <DialogTitle>MuunganoMoney</DialogTitle>
     <DialogContent>

      <DialogContentText>
       Numero de télèphone n'est pas enregistre chez MuunganoMoney.ou Incorrect
     </DialogContentText>

     </DialogContent>
     <DialogActions>
      <Button onClick={handleClose}>Fermer</Button>
     </DialogActions>
    </Dialog>

    <button className='Btn'>Suivant</button>
   </form>
  </>
 );
};
