import React from 'react';
import { IoSend } from 'react-icons/io5';
import './Btn.css';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../firebase';


// Button blocked
export default function ReturnBtn() {

 let pushDocsA = new Array();
 const navigation = useNavigate();

 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocsA.push(doc.id);
  });

  const verifierCollection = pushDocsA.some(value => value == JSON.parse(window.localStorage.getItem('**adm@@ph$$&-@#')));

  try {
   const unsub = onSnapshot(doc(db, verifierCollection ? "client" : "agent", JSON.parse(window.localStorage.getItem('**adm@@ph$$&-@#'))), (doc) => {
    setFirst(doc.data().firstname);
    setLast(doc.data().lastname);
   });

  } catch {
   window.alert('error window');
  }



 }, []);

 const name = first + ' ' + last;

 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('#adX**˙´ð..¸', JSON.stringify(true));
  window.localStorage.setItem('#adX**nam**', JSON.stringify(name));
  navigation('/send/franc');
 };

 return (
  <button style={{ paddingLeft: '1.5em' }} onClick={handlepath} className={'Btn'}>
   <span>
    Suivant
   </span>
   <div><IoSend size={'1.5em'} color={'transparent'} /></div>
  </button>
 );
};
