import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, getDoc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";
import { db } from '../../firebase';
import REturnQuOte from './Quote';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

import moment from 'moment';

export let nowField = moment().date();
export let now = moment().date();

export let prixHash = 0;
export let hashTrue = true;


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-form-input-pin'>
  <REturnQuOte />
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-form-input-pin'>
  <REturnQuOte />
  <FormInput />
 </div>
);

export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm();

 const [getPin, setGetPin] = React.useState('');
 const [solde, setSolde] = React.useState(null);

 const [open, setOpen] = React.useState(false);
 const [passe, setPasse] = React.useState(false);

 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 // const handleClickShowPassword = () => setShowPassword((show) => !show);
 // const handleMouseDownPassword = (event) => {
 //  event.preventDefault();
 // };

 const handleClose = () => {
  setOpen(false);
 };

 const handlePasseClose = () => {
  setPasse(false);
 };


 React.useEffect(async () => {

  try {

   await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
    setGetPin(doc.data().code);
    setSolde(doc.data().cdf);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  const phoneItem = window.localStorage.getItem('PHONE_SEND');
  const sendEMCdf = window.localStorage.getItem('SEND_EM_CDF');
  const sendEMAgentCdf = window.localStorage.getItem('SEND_EM_AGENT_CDF');
  const sendUser = window.localStorage.getItem('SEND_USER');

  if (getPin == data.code) {

   if (data.count > 500000 || data.count < 1000 || solde < 1000 || data.count > solde || regular.test(data.count)) {
    setOpen(true);
    reset();
   } else {

    // let User = first + ' ' + last;
    sendAgentMoney(Math.floor(Number(data.count)), JSON.parse(phoneItem));
    updateAdmin(Number(data.count), Math.floor(Number(data.count)), JSON.parse(sendUser));


    let arraySendEMCdf = JSON.parse(sendEMCdf);
    let arraySendEMAgentCdf = JSON.parse(sendEMAgentCdf);

    let soldeEmCDF = Number(Math.max(...arraySendEMCdf)) + Number(data.count);
    let soldeAgentCDF = Number(Math.max(...arraySendEMAgentCdf)) + Number(data.count);

    let arrayEmCdf = arraySendEMCdf;
    let arrayAgentCdf = arraySendEMAgentCdf;

    for (let i = now; i <= 31; i++) {
     arrayEmCdf[i] = soldeEmCDF;
     arrayAgentCdf[i] = soldeAgentCDF;
    }

    prixSuccess = data.count;

    updateMuungano(arrayEmCdf, arrayAgentCdf);
    // window.localStorage.setItem('PHONE_SEND', JSON.stringify(null));
    // window.localStorage.setItem('SEND_EM_CDF', JSON.stringify(null));
    // window.localStorage.setItem('SEND_EM_AGENT_CDF', JSON.stringify(null));
    // window.localStorage.setItem('#62^^ts@&', JSON.stringify(false));

    navigation('/send/success');
   }
  } else {
   prixSuccess = data.count;
   setPasse(true);
   reset();
  }

 };


 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <FormControl
    sx={{ width: '100%' }}

    variant="standard">
    <InputLabel htmlFor="standard-adornment-password">Pin actuel</InputLabel>

    <Controller
     name="code"
     control={control}
     render={({ field }) =>

      <Input
       id="standard-adornment-password"
       autocomplete="new-password"
       {...field}
       type={showPassword ? 'text' : 'password'}

       endAdornment={
        <InputAdornment position="end">

         <IconButton
          aria-label="toggle password visibility"
          onClick={handleClickShowPassword}
          onMouseDown={handleMouseDownPassword}
         >
          {showPassword ? <VisibilityOff /> : <Visibility />}
         </IconButton>

        </InputAdornment>
       }

      />}
    />
   </FormControl>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={passe}
    onClose={handlePasseClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Veuillez vérifier le mot de passe administrateur
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handlePasseClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le solde entre n'est pas valide, veuillez vérifier
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>


   <button className='Btn'>Envoi</button>
  </form>
 )
};



async function sendAgentMoney(solde, numPhone) {

 let time = moment().format('LLL');
 let send = { date: time, solde: `${solde} CDF`, phone: numPhone, user: 'muunganomoney', type: 'réussi' }

 const washingtonRef = doc(db, "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  cdf: increment(solde),
  money: solde,
  unite: 'CDF',
  user: 'muunganomoney',
  delay: moment().format(),
  swap: arrayUnion(send)
 });
};
async function updateAdmin(solde, money, User) {

 const washingtonRef = doc(db, "muunganomoney", "money");
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  cdf: increment(-(solde)),
  money: money,
  buycdf: solde,
  unite: 'CDF',
  user: User,
  delay: moment().format()
 });

 const emoneyRef = doc(db, "muunganomoney", "e-money");
 await updateDoc(emoneyRef, {
  cdf: solde
 });

 const moneyRef = doc(db, "muunganomoney", "money");
 await updateDoc(moneyRef, {
  cdf: increment(-(solde))
 });

};
async function updateMuungano(emCdf, agentCdf) {

 const emoneyRef = doc(db, "muungano", "emoney");
 await updateDoc(emoneyRef, {
  cdf: emCdf,
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  cdf: agentCdf,
 });

};
