import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';

import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';

import PropTypes from 'prop-types';
import { IMaskInput } from 'react-imask';
import { NumericFormat } from 'react-number-format';
import TextField from '@mui/material/TextField';


const TextMaskCustom = React.forwardRef(function TextMaskCustom(props, ref) {
 const { onChange, ...other } = props;
 return (
  <IMaskInput
   {...other}
   mask="(#00) 000-0000"
   definitions={{
    '#': /[1-9]/,
   }}
   inputRef={ref}
   onAccept={(value) => onChange({ target: { name: props.name, value } })}
   overwrite
  />
 );
});
TextMaskCustom.propTypes = {
 name: PropTypes.string.isRequired,
 onChange: PropTypes.func.isRequired,
};
const NumericFormatCustom = React.forwardRef(function NumericFormatCustom(
 props,
 ref,
) {
 const { onChange, ...other } = props;

 return (
  <NumericFormat
   {...other}
   getInputRef={ref}
   onValueChange={(values) => {
    onChange({
     target: {
      name: props.name,
      value: values.value,
     },
    });
   }}
   thousandSeparator
   valueIsNumericString
   prefix="(F)"
  />
 );
});
NumericFormatCustom.propTypes = {
 name: PropTypes.string.isRequired,
 onChange: PropTypes.func.isRequired,
};


// Firebase auth 
export let phoneX = '';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='input-phone-register'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='input-phone-register'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let clientDocs = new Array();
 let agentDocs = new Array();
 let regular = /[a-z]+/;
 const [load, setLoad] = React.useState(false);

 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm({ defaultValues: { phone: '+243' } });

 const [open, setOpen] = React.useState(false);
 const [error, setError] = React.useState(false);

 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');


 const [values, setValues] = React.useState({
  textmask: '(100) 000-0000',
  numberformat: '1320',
 });
 const handleChange = (event) => {
  setValues({
   ...values,
   [event.target.name]: event.target.value,
  });
 };



 const handleClose = () => {
  setOpen(false);
 };
 const handleError = () => {
  setError(false);
 };

 const onSubmit = async (data) => {

  setLoad(true);

  if (data.phone.length != 13 || regular.test(data.phone)) {
   setError(true);
   setLoad(false);
   reset();

  } else {

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    clientDocs.push(doc.id);
   });

   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    agentDocs.push(doc.id);
   });

   const checkedNumPhoneClient = clientDocs.some(value => value == data.phone);
   const checkedNumPhoneAgent = agentDocs.some(value => value == data.phone);

   if (checkedNumPhoneClient || checkedNumPhoneAgent) {
    setOpen(true);
    setLoad(false);
    reset();
   } else {

    phoneX = data.phone;
    window.localStorage.setItem('%%regi&*$%', JSON.stringify(true));
    window.localStorage.setItem('PHONE_REGISTER', JSON.stringify(data.phone));
    navigation('/register/signin');
   }

  };
 };

 return (
  <>
   <div className='zindex-theme'>
    <Backdrop
     sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
     open={load}>

     <CircularProgress color="inherit" />
    </Backdrop>
   </div>

   <form onSubmit={handleSubmit(onSubmit)}>


    <FormControl
     sx={{ width: '100%' }}

     variant="standard">
     <InputLabel htmlFor="standard-adornment-password">Téléphone</InputLabel>

     <Controller
      name="phone"
      defaultValue='+243'
      control={control}
      render={({ field }) =>

       <Input
        id="standard-adornment-password"
        {...field}

        className='text-input-size'
        type='phone'

       />}
     />

    </FormControl>


    <Dialog
     fullWidth={fullWidth}
     maxWidth={maxWidth}
     open={error}
     onClose={handleError}
    >
     <DialogTitle>MuunganoMoney</DialogTitle>
     <DialogContent>

      <DialogContentText>
       Le numéro de téléphone a une erreur
     </DialogContentText>

     </DialogContent>
     <DialogActions>
      <Button onClick={handleError}>Fermer</Button>
     </DialogActions>
    </Dialog>

    <Dialog
     fullWidth={fullWidth}
     maxWidth={maxWidth}
     open={open}
     onClose={handleClose}
    >
     <DialogTitle>MuunganoMoney</DialogTitle>
     <DialogContent>

      <DialogContentText>
       Numero de télèphone est déja enregistre chez Muunganomoney
     </DialogContentText>

     </DialogContent>
     <DialogActions>
      <Button onClick={handleClose}>Fermer</Button>
     </DialogActions>
    </Dialog>

    <button className='Btn'>Suivant</button>

   </form>
  </>
 );
};
