import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';

import { VscChevronRight } from 'react-icons/vsc';


// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btn-remove'>
  <VieWBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-remove-sm'>
  <VieWBtn />
 </div>
);
export const VieWBtn = () => (
 <BtnNexT />
);

export const BtnNexT = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('BUILD', JSON.stringify(false));
  navigation('/dash');
 }
 return (
  <button onClick={handlepath} className='Btn'>
   <span>
    Suivant
  </span>
   <span>
    <VscChevronRight size={'2em'} />
   </span>
  </button>
 );
};


