import React from 'react';
import { IoSend } from 'react-icons/io5';
import './Btn.css';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs, doc, getDoc } from "firebase/firestore";

import { db } from '../firebase';
import { phoneX } from '../Sign/Input';
import Media from 'react-media';


// Button blocked
export default function ReturnBtn() {
 <Media
  queries={{
   small: '(max-width: 599px)',
   medium: '(min-width: 600px) and (max-width:1199px)',
   large: '(min-width: 1200px)',
  }}>
  {matches => (
   <>
    {matches.small && <ScreenSmall />}
    {matches.medium && <ScreenLarge />}
    {matches.large && <ScreenLarge />}
   </>
  )}
 </Media>
};

export const ScreenLarge = () => (
 <div className='wrp-btn-valid-exh'>
  <Btn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-valid-exh-sm'>
  <Btn />
 </div>
);


export const Btn = () => {

 let pushDocs = new Array();
 const [state, setState] = React.useState();

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/send-usd');
 };

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });
  const verifier = pushDocs.some(value => value == phoneX);

  const docRef = doc(db, verifier ? "client" : "agent", phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setState(docSnap.data().state)
  }

 }, []);

 return (
  <div onClick={handlepath}>
   <button className={'Btn'}>
    <span>
     {state == "client" ? "Retrait" : "Envoyer"}
    </span>
    <div><IoSend size={'1em'} /></div>
   </button>
  </div>
 );
};
