import React from 'react';
import './Date.css';
import { doc, getDoc } from "firebase/firestore";
import { db } from '../../firebase';

// Return Date now Component 
export default function ReturndAshdAte() {

 let now = "";
 // const [now, setNow] = React.useState(null);
 React.useEffect(async () => {

  const docRef = doc(db, "ungama", "money");
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   now = docSnap.data().delay
  }

 }, []);

 return (
  <div className='wrp-date-trade-dash'>
   <h2>Date</h2>
   <p>{now}</p>
  </div>
 );
};
