import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, getDoc, updateDoc, serverTimestamp, increment, } from "firebase/firestore";

import { db } from '../../firebase';

export let prixSuccess = 0;

// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let newSolde = 0;

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const [getPin, setGetPin] = React.useState('');
 // const [getFc, setGetFc] = React.useState(null);
 // const [user, setUser] = React.useState(null);
 // const [getFcAdmin, setGetFcAdmin] = React.useState(0);
 // const [actif, setActif] = React.useState(0);

 React.useEffect(async () => {

  const docRefPin = doc(db, "ungama", "money");
  const docSnap = await getDoc(docRefPin);

  if (docSnap.exists()) {
   setGetPin(docSnap.data().pin);
   setGetFcAdmin(docSnap.data().fc);
   setActif(docSnap.data().allcdf);
  };

  const agentRef = doc(db, "agent", phoneX);
  const agentSnap = await getDoc(agentRef);

  if (agentSnap.exists()) {
   setGetFc(Number(agentSnap.data().fc));
   setUser(agentSnap.data().firstname);
  }

 }, []);

 const onSubmit = async (data) => {

  if (getPin == data.code && data.code.length == 6) {

   if (data.count > 200000) {
    window.alert(`La valeur est superieur à 200000`);
    reset();

   } else {

    // addSoldeAgent(phoneX, Number(getFc) + Number(data.count), data.count);

    // newSolde = Number(getFcAdmin) - Number(data.count);
    // let newActif = Number(actif) + Number(data.count);
    // updateSolde(data.count, newSolde, newActif, user);

    sendAgentMoney(Math.floor(Number(data.count)), Math.floor(Number(data.count)), phoneX)

    prixSuccess = data.count;
    navigation('/send/success');
   }

  } else {
   prixSuccess = data.count;
   window.alert(`Erreur de code d'accès invalide`)
   reset();
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'CDF'}
   />
   <input type={'text'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};


