import React from 'react';
import './Prix.css';
import { Fc, Usd, Current } from '../../../Money';


// Prix HeAd 
export default function ReturnPrix() {

 const [fcCongo, setFcCongo] = React.useState(Fc);
 const [usdAmerica, setUsdAmerica] = React.useState(Usd);
 const [fcCurrent, setFcCurrent] = React.useState(Current);

 return (
  <div className='wrp-main-prix-dash'>
   <ReturnPrixUseR prix={fcCurrent} unite={'FC'} />
   <ReturnPrixUseR prix={usdAmerica} unite={'USD'} coloor={true} coolorUnite={true} />
   <ReturnPrixUseR prix={fcCongo} coloor={true} coolorUnite={true} unite={'FC'} />
  </div>
 );
};
export function ReturnPrixUseR(props) {
 return (
  <div className='wrp-prix-dash'>
   <span style={{ color: `${props.coloor === true ? '#ffeb3b' : '#9e9e9e'}` }}>{props.prix}</span>
   <span style={{ color: `${props.coolorUnite === true ? '#ffeb3b' : '#9e9e9e'}` }}>{props.unite}</span>
  </div>
 );
};
