import React from 'react';
import moment from 'moment';
import { doc, onSnapshot } from "firebase/firestore";
import { db } from '../../firebase';


let value = 0;

export const ReturnAgenT = () => {

 const [prix, setPrix] = React.useState([]);
 React.useEffect(async () => {
  try {
   onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setPrix(doc.data().agent);
   });
  } catch {
   window.console.log('erreur disponible')
  }
 }, []);

 return (
  <div className='wrp-time-sub' >
   {[...Array(moment().date()).keys()].map(index => {

    value = index;
    value += 1;

    return (
     <h1 style={{ color: '#2196f3' }}>{prix[value]}</h1>
    )
   })
   }
  </div>
 );
};
