import React from 'react';
import ReturnSuCCess from './Success';
import BarLoader from 'react-spinners/BarLoader';
import { auth } from '../../firebase';
import { useNavigate } from 'react-router-dom';

// Return Main Success
export default function SuCCess() {

 const user = auth.currentUser;
 const [loading, setLoading] = React.useState(true);
 const [people, setPeople] = React.useState(user);

 React.useEffect(() => {

  window.localStorage.setItem('#62^^ts@&', JSON.stringify(true));
  JSON.parse(window.localStorage.getItem('#62^^ts@&'))

  // setLoading(true);
  setTimeout(() => {
   setLoading(false);
  }, 3600);

  if (user) {
   setTimeout(() => {
    setPeople(true);
   }, 600);

  }

 }, []);

 return (
  <div>
   {loading && <div className='App-loading'></div>}
   {loading && <div className='App-Icon'>
    <BarLoader
     size={15}
     color={'#00b8d4'}
     loading={loading}
    />
   </div>}

   <>
    {people && <ReturnSuCCess />}
   </>
  </div>
 );
};
