import React from 'react';
import './Quote.css';
import Media from 'react-media';
import { TweenMax, Expo } from 'gsap';

// Retur Quote Component
export default function ReturnQuote() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 )
};


export const ScreenLarge = () => (
 <div className='wrp-quote-reset'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-quote-reset-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 React.useEffect(() => {
  TweenMax.from('.Anima', 1, { delay: 0.4, opacity: 0, y: 10, ease: Expo.easeIn });

 }, []);

 return (
  <p className='Anima'>Vous pouvez d√©finir un nouveau mot de passe administrateur</p>
 );
};
