import React from 'react';
import './Prix.css';
import Media from 'react-media';
import { TweenLite } from 'gsap';
import { exchAngeBalance } from './Input';


// Global Money
export let Balance = 0;


// REturn view Prix available
export default function REturnPriX(props) {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall count={props.count} />}
     {matches.medium && <ScreenLarge count={props.count} />}
     {matches.large && <ScreenLarge count={props.count} />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = (props) => (
 <div className='wrp-prix-withdraw'>
  <WithdrawBalance count={props.count} />
 </div>
);
export const ScreenSmall = (props) => (
 <div className='wrp-prix-withdraw'>
  <WithdrawBalance count={props.count} />
 </div>
);

export const WithdrawBalance = (props) => {
 const checkedcount = props.count;

 const [withdraw, setWithdraw] = React.useState(exchAngeBalance);
 const obj = { value: 0 };

 React.useEffect(() => {

  let target = document.querySelector('.wrp-prix-Fran');
  TweenLite.to(obj, 2, {
   value: withdraw,
   roundProps: { value: 20 },
   onUpdate: () => target.innerHTML = obj.value
  })

  Balance = withdraw;
  console.log(withdraw);
 }, []);

 return (
  <>
   <h1>Balance</h1>
   <p className='wrp-mainprix-money'>
    <span className='wrp-prix-Fran' style={{ color: `${checkedcount < 0 ? '#e0e0e0' : 'black'}` }}></span>
    <span>FC</span>
   </p>
  </>
 )
};
