import React from 'react';
import './Time.css';
import REturnPriX from './Prix';
import Media from 'react-media';
import { doc, onSnapshot, getDocs, collection, updateDoc, increment } from "firebase/firestore";
import { phoneX } from '../Sign/Input';
import { db } from '../firebase';
import moment from 'moment';

export let exchAnge = 0; // Export view 

// Return Phone input component
export default function REturnInputPhone() {
 return (
  <>
   <Media
    queries={{
     small: '(max-width: 599px)',
     medium: '(min-width: 600px) and (max-width:1199px)',
     large: '(min-width: 1200px)',
    }}>
    {matches => (
     <>
      {matches.small && <ScreenSmall />}
      {matches.medium && <ScreenLarge />}
      {matches.large && <ScreenLarge />}
     </>
    )}
   </Media>
  </>
 );
};

export const ScreenLarge = () => {
 return (
  <>
   <REturnPriX />
   <div className='wrp-frais-current'>
    <FormInputValue />
   </div>
  </>
 );
};
export const ScreenSmall = () => {
 return (
  <>
   <REturnPriX />
   <div className='wrp-frais-current-sm'>
    <FormInputValue />
   </div>
  </>
 )
};
export const FormInputValue = () => {

 let pushArray = new Array();
 let endMonth = moment().endOf('month');

 const [state, setState] = React.useState('');
 const [prixusd, setPrixusd] = React.useState(null);
 const [prixcdf, setPrixcdf] = React.useState(null);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushArray.push(doc.id);

  });

  const verifier = pushArray.some(index => index == phoneX);
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", phoneX), (doc) => {
   setState(doc.data().state);
   setPrixusd(doc.data().thriftusd);
   setPrixcdf(doc.data().thriftcdf);
  });

  if (moment().isSame(endMonth)) {
   IncrementThriftMoney(prixusd, prixcdf, state, phoneX);
   window.setTimeout(() => {
    DecrementThriftMoney(state, phoneX);
   }, 3000);

  }

 }, []);

 return (
  <>
   <span>{`Compte d'Ã©pargne disponible`}</span>
   <span>{`${endMonth.locale('fr').format('LLLL')}`}</span>
  </>
 );
};

async function IncrementThriftMoney(soldeUsd, soldeCdf, status, phoneNumber) {

 const savingsRef = doc(db, `${status}`, phoneNumber);
 await updateDoc(savingsRef, {
  cdf: increment(soldeCdf),
  usd: increment(soldeUsd),
 });

};
async function DecrementThriftMoney(status, phoneNumber) {

 const savingsRef = doc(db, `${status}`, phoneNumber);
 await updateDoc(savingsRef, {
  thriftcdf: 0,
  thriftusd: 0,
 });

};
