import React from 'react';
import './Title.css';
import Media from 'react-media';
import { TweenMax, Power2 } from 'gsap';

// view compoen title
export default function REturnTitle() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmAll />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => {

 React.useEffect(() => {

  TweenMax.from('.flx-content-ima h1', .8, { delay: 1, opacity: 0, y: 40, ease: Power2.easeIn })
  TweenMax.from('.flx-content-ima p', .8, { delay: 1.2, opacity: 0, y: 40, ease: Power2.easeIn })

 }, [])

 return (
  <div className='flx-content-ima'>
   <ViewTitle />
  </div>
 )
}
export const ScreenSmAll = () => {
 return (
  <div className='flx-content-ima-sm'>
   <ViewTitle />
  </div>
 )
}

const ViewTitle = () => (
 <>
  <h1>Make deposits and withdrawals with 0% commision</h1>
  <p>Coinbase is the easiest and most trusted place to buy, sell and manage your digital currency.</p>
 </>
);
