import React from 'react';
import ReturnRegisterSuccess from './RegisterSuccess';
import BarLoader from 'react-spinners/BarLoader';
import { auth } from '../../firebase';

// Register Full Success
export default function RegisterSuccess() {

 const user = auth.currentUser;
 const [loading, setLoading] = React.useState(true);
 const [people, setPeople] = React.useState(user);

 React.useEffect(() => {

  // setLoading(true);
  setTimeout(() => {
   setLoading(false);
  }, 3600);

  if (user) {
   setTimeout(() => {
    setPeople(true);
   }, 600);

  }

 }, []);

 return (
  <div>
   {loading && <div className='App-loading'></div>}
   {loading && <div className='App-Icon'>
    <BarLoader
     size={15}
     color={'#00b8d4'}
     loading={loading}
    />
   </div>}

   <>
    {people && <ReturnRegisterSuccess />}
   </>
  </div>
 );
};
