import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, getDoc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";
import { db } from '../../firebase';
import REturnQuOte from './Quote';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

import moment from 'moment';

export let nowField = moment().date();
export let now = moment().date();

export let prixHash = 0;
export let hashTrue = true;


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-form-input-pin'>
  <REturnQuOte />
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-form-input-pin'>
  <REturnQuOte />
  <FormInput />
 </div>
);

export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm();

 //  order notebook
 const [tableClient, setTableClient] = React.useState();
 const [tableAgent, setTableAgent] = React.useState();
 const [tableUpgrade, setTableUpgrade] = React.useState();
 const [tableAdmin, setTableAdmin] = React.useState();
 const [tableFrais, setTableFrais] = React.useState();

 // send React useState  
 const [pin, setPin] = React.useState(null);
 const [state, setState] = React.useState(null);
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');
 const [soldeMain, setSoldeMain] = React.useState(null);

 // getting React useState
 const [getstate, setGetstate] = React.useState(null);
 const [getfirst, setGetFirst] = React.useState('');
 const [getlast, setGetLast] = React.useState('');


 const [open, setOpen] = React.useState(false);
 const [cancel, setCancel] = React.useState(false);
 const [click, setClick] = React.useState(false);

 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const [showPassword, setShowPassword] = React.useState(false);

 const handleClickShowPassword = () => setShowPassword((show) => !show);
 const handleMouseDownPassword = (event) => {
  event.preventDefault();
 };

 const handleClose = () => {
  setOpen(false);
 };
 const cancelClose = () => {
  setCancel(false);
  setClick(true);
 };


 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <FormControl
    sx={{ width: '100%' }}

    variant="standard">
    <InputLabel htmlFor="standard-adornment-password">Pin actuel</InputLabel>

    <Controller
     name="code"
     control={control}
     render={({ field }) =>

      <Input
       id="standard-adornment-password"
       autocomplete="new-password"
       {...field}
       type={showPassword ? 'text' : 'password'}

       endAdornment={
        <InputAdornment position="end">

         <IconButton
          aria-label="toggle password visibility"
          onClick={handleClickShowPassword}
          onMouseDown={handleMouseDownPassword}
         >
          {showPassword ? <VisibilityOff /> : <Visibility />}
         </IconButton>

        </InputAdornment>
       }

      />}
    />

   </FormControl>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}>

    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le code PIN a été vérifié, il est incorrect
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={cancel}
    onClose={cancelClose}>

    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Des frais sont appliqués à cette transaction. Merci de choisir Muunganomoney
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={cancelClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

   <button className='Btn'>Envoi</button>
  </form>
 )
};

async function swapInWithDocsAgent(sendPhone, getPhone, sendUser, getUser, main, money, frais, unite, arrayAgentMoney, upgrade, adminFrais) {

 let time = moment().format('LLL');
 let send = { date: time, solde: `${money} ${unite}`, phone: getPhone, user: getUser, type: 'envoyer' }
 let get = { date: time, solde: `${money} ${unite}`, phone: sendPhone, user: sendUser, type: 'réussi' }


 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  cdf: (main),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format(),
  swap: arrayUnion(send)
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  cdf: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format(),
  swap: arrayUnion(get)
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  cdf: arrayAgentMoney,
 });

 const upgradeRef = doc(db, "muungano", "upgrade");
 await updateDoc(upgradeRef, {
  cdf: upgrade,
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  spredcdf: increment(Math.floor(Number(frais)))
 });

 const fraisRef = doc(db, "muungano", "commission");
 await updateDoc(fraisRef, {
  cdf: adminFrais,
 });

};
async function swapInWithDocsAgentToClient(sendPhone, getPhone, sendUser, getUser, prix, unite, arrayAgentMoney, upgrade) {

