import React from 'react';
import './Time.css';
import REturnPriX from './Prix';
import Media from 'react-media';

import { doc, getDoc, collection, getDocs } from "firebase/firestore";
import { phoneX } from '../Sign/Input';
import { db } from '../firebase';
import moment from 'moment';

export let exchAnge = 0; // Export view 

// Return Phone input component
export default function REturnInputPhone() {
 return (
  <>
   <Media
    queries={{
     small: '(max-width: 599px)',
     medium: '(min-width: 600px) and (max-width:1199px)',
     large: '(min-width: 1200px)',
    }}>
    {matches => (
     <>
      {matches.small && <ScreenSmall />}
      {matches.medium && <ScreenLarge />}
      {matches.large && <ScreenLarge />}
     </>
    )}
   </Media>
  </>
 );
};

export const ScreenLarge = () => {
 return (
  <>
   <REturnPriX />
   <div className='wrp-frais-current'>
    <FormInputValue />
   </div>
  </>
 );
};
export const ScreenSmall = () => {
 return (
  <>
   <REturnPriX />
   <div className='wrp-frais-current-sm'>
    <FormInputValue />
   </div>
  </>
 )
};
export const FormInputValue = () => {

 let pushDocs = new Array();
 const [delay, setDelay] = React.useState(null);

 React.useEffect(async () => {
  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  const verifierCollection = pushDocs.some((value) => value == phoneX);
  const docRef = doc(db, verifierCollection ? "client" : "agent", phoneX);

  const docSnap = await getDoc(docRef);
  if (docSnap.exists()) {
   setDelay(docSnap.data().thriftdate);
  } else {
   console.log('Erreur Date');
  }
 }, []);

 return (
  <>
   <span>{`Compte epargne disponible`}</span>
   <span>{delay}</span>
  </>
 );
};
