import './Quote.css';
import Media from 'react-media';

// Return Quote Component 
export default function ReturnQuOte() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-quote-hm-withdraw'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-quote-hm-withdraw-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => (
 <p>
  Toute entreprise qui accepte les cartes de crédit ou PayPal
  connaît le problème des paiements qui sont ensuite annulés.
  lorsqu'il est utilisé Ungamamoney, vous permet de payer avec un simple scan et paiement en deux étapes.
 </p>
);
