import React from 'react';
import './BtnRegister.css';
import { IoIosPeople } from 'react-icons/io';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
// import ReturnLogout from './ signOut';
// import { phoneX } from '../../../Sign/Input';

import { collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';


// Button Register 
export default function ReturnBtnRegister() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-btn-register-dash'>
  <ConexionRegister />
  {/* <ReturnLogout /> */}
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-register-dash-sm'>
  <ConexionRegister />
  {/* <ReturnLogout /> */}
 </div>
);


export const RegisterBtn = () => {

 const [checked, setChecked] = React.useState(true);

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/register');
 };


 return (
  <>
   {checked ?
    <button onClick={handlepath} className='Btn'>
     <span>
      Ajouter Client
   </span>
     <IoIosPeople size={'2.5em'} color='grey' />
    </button>
    :
    <div></div>
   }
  </>
 );
};

