import React from 'react';
import './Name.css';
import Media from 'react-media';
import { phoneX } from '../Phone/Input';
import { doc, getDoc } from "firebase/firestore";
import { db } from '../../firebase';


// Return view Name
export default function ReturnName() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-name-insend'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-name-insend-sm'>
  <ViewText />
 </div>
);

export const ViewText = () => {

 let first = '';
 let last = '';

 return (
  <h2>{first} {last}</h2>
 );
};

async function User(phoneDocs) {

 const docRef = doc(db, "agent", phoneDocs);
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  nom = docSnap.data().firstname;
  prenom = docSnap.data().lastname;


  return { nom, prenom }

 } else {
  console.log("No such document!");
 }
}
